export const exportToCSV = (sheet: Sheet) => {
  const bom = "\uFEFF"; // Byte Order Mark for Arabic support in Excel
  const totalSpent = sheet.lines.reduce((sum, line) => sum + line.amount + (line.bank_fees || 0), 0);
  const remaining = sheet.custody_amount - totalSpent;

  // Header Summary
  let csvContent = `ุฑูู ุงูุนูุฏุฉ,ูุจูุบ ุงูุนูุฏุฉ,ุงูุฅุฌูุงูู ุงูููุตุฑู,ุงููุชุจูู\n`;
  csvContent += `${sheet.custody_number},${sheet.custody_amount},${totalSpent},${remaining}\n\n`;

  // Table Headers
  csvContent += `ุงูุชุงุฑูุฎ,ุงูุดุฑูุฉ,ุฑูู ุงููุงุชูุฑุฉ,ุงูุจูุงู,ุณุจุจ ุงูุตุฑู,ุงููุจูุบ,ูุตุฑููุงุช ุจูููุฉ,ุงูุฅุฌูุงูู,ุงุณู ุงููุดุชุฑู,ููุงุญุธุงุช\n`;

  // Rows
  sheet.lines.forEach(line => {
    const lineTotal = line.amount + (line.bank_fees || 0);
    const row = [
      line.date,
      `"${line.company.replace(/"/g, '""')}"`, // Escape quotes
      `"${(line.invoice_number || '').replace(/"/g, '""')}"`,
      `"${line.description.replace(/"/g, '""')}"`,
      line.reason,
      line.amount,
      line.bank_fees || 0,
      lineTotal,
      `"${(line.buyer_name || '').replace(/"/g, '""')}"`,
      `"${(line.notes || '').replace(/"/g, '""')}"`
    ].join(",");
    csvContent += row + "\n";
  });

  const blob = new Blob([bom + csvContent], { type: "text/csv;charset=utf-8;" });
  const link = document.createElement("a");
  const url = URL.createObjectURL(blob);
  link.setAttribute("href", url);
  link.setAttribute("download", `custody_sheet_${sheet.custody_number}_${new Date().toISOString().slice(0, 10)}.csv`);
  document.body.appendChild(link);
  link.click();
  document.body.removeChild(link);
};

import { Sheet, ExpenseLine, SheetStatus } from '../types';

// In a real app, this would use fetch/axios. Here we simulate using LocalStorage
// to maintain state across reloads for the demo.

const STORAGE_KEY = 'instant-expenses-sheets-cache';

export const getSheets = async (): Promise<Sheet[]> => {
  // Simulate network delay
  await new Promise(resolve => setTimeout(resolve, 500));
  const stored = localStorage.getItem(STORAGE_KEY);
  return stored ? JSON.parse(stored) : [];
};

export const saveSheet = async (sheet: Sheet): Promise<Sheet> => {
  await new Promise(resolve => setTimeout(resolve, 300));
  const sheets = await getSheets();
  const existingIndex = sheets.findIndex(s => s.id === sheet.id);

  if (existingIndex >= 0) {
    sheets[existingIndex] = { ...sheet, last_modified: new Date().toISOString() };
  } else {
    sheets.push({ ...sheet, last_modified: new Date().toISOString() });
  }

  localStorage.setItem(STORAGE_KEY, JSON.stringify(sheets));
  return sheet;
};

export const createNewSheet = (number: string, amount: number, empId: string): Sheet => {
  return {
    id: `CUST-${Math.floor(Math.random() * 10000)}`,
    custody_number: number,
    custody_amount: amount,
    employee_id: empId,
    status: SheetStatus.OPEN,
    lines: [],
    created_at: new Date().toISOString(),
    last_modified: new Date().toISOString(),
  };
};

