interface ExpenseTableProps {
  sheet: Sheet;
  onAddLine: (line: ExpenseLine) => void;
  onUpdateLine: (line: ExpenseLine) => void;
  onDeleteLine: (lineId: string) => void;
  reasonFilter: ExpenseReason | 'ALL';
}

const EmptyDraftLine: Omit<ExpenseLine, 'id' | 'sheet_id' | 'created_at'> = {
  date: new Date().toISOString().slice(0, 10),
  company: '',
  invoice_number: '',
  description: '',
  reason: ExpenseReason.PROJECTS,
  amount: 0,
  bank_fees: 0,
  buyer_name: '',
  notes: ''
};

export const ExpenseTable: React.FC<ExpenseTableProps> = ({
  sheet,
  onAddLine,
  onUpdateLine,
  onDeleteLine,
  reasonFilter
}) => {
  const [draft, setDraft] = useState(EmptyDraftLine);
  const [isEditingId, setIsEditingId] = useState<string | null>(null);

  // Filter lines
  const visibleLines = reasonFilter === 'ALL' 
    ? sheet.lines 
    : sheet.lines.filter(l => l.reason === reasonFilter);

  const handleDraftChange = (field: keyof typeof draft, value: any) => {
    setDraft(prev => ({ ...prev, [field]: value }));
  };

  const handleSubmitDraft = () => {
    if (!draft.company || !draft.description || draft.amount <= 0) {
      alert("ุงูุฑุฌุงุก ุชุนุจุฆุฉ ุงูุญููู ุงูุฅูุฒุงููุฉ: ุงูุดุฑูุฉุ ุงูุจูุงูุ ุงููุจูุบ (>0)");
      return;
    }

    if (isEditingId) {
        // Find original created_at to preserve it or use now
        const originalLine = sheet.lines.find(l => l.id === isEditingId);
        const newLine: ExpenseLine = {
            ...draft,
            id: isEditingId,
            sheet_id: sheet.id,
            created_at: originalLine?.created_at || new Date().toISOString()
        };
        onUpdateLine(newLine);
        setIsEditingId(null);
    } else {
        const newLine: ExpenseLine = {
            ...draft,
            id: generateId(),
            sheet_id: sheet.id,
            created_at: new Date().toISOString()
        };
        onAddLine(newLine);
    }

    // Reset draft but keep date and buyer for convenience
    setDraft({
      ...EmptyDraftLine,
      date: draft.date,
      buyer_name: draft.buyer_name
    });
  };

  const handleEditClick = (line: ExpenseLine) => {
    // Move line data to draft area
    setDraft({
      date: line.date,
      company: line.company,
      invoice_number: line.invoice_number || '',
      description: line.description,
      reason: line.reason,
      amount: line.amount,
      bank_fees: line.bank_fees || 0,
      buyer_name: line.buyer_name || '',
      notes: line.notes || ''
    });
    setIsEditingId(line.id);

    // Smooth scroll to bottom
    setTimeout(() => {
        document.getElementById('draft-row')?.scrollIntoView({ behavior: 'smooth' });
    }, 100);
  };

  const handleCancelEdit = () => {
    setIsEditingId(null);
    setDraft({
        ...EmptyDraftLine,
        date: draft.date,
        buyer_name: draft.buyer_name
    });
  };

  const handleKeyDown = (e: React.KeyboardEvent) => {
    if (e.key === 'Enter') {
      handleSubmitDraft();
    }
