import React, { useState, useEffect, useCallback } from 'react';
import { Sheet, ExpenseLine, ExpenseReason } from './types';
import { getSheets, saveSheet, createNewSheet, checkHealth } from './services/expenseService';
import { DEFAULT_USER, REASON_OPTIONS } from './constants';
import { exportToCSV } from './utils';

import { ConnectionBar } from './components/ConnectionBar';
import { SheetList } from './components/SheetList';
import { SheetHeader } from './components/SheetHeader';
import { ExpenseTable } from './components/ExpenseTable';
import { Filter } from 'lucide-react';

const App: React.FC = () => {
  const [isOnline, setIsOnline] = useState(true);
  const [sheets, setSheets] = useState<Sheet[]>([]);
  const [activeSheet, setActiveSheet] = useState<Sheet | null>(null);
  const [isSaving, setIsSaving] = useState(false);
  const [filterReason, setFilterReason] = useState<ExpenseReason | 'ALL'>('ALL');

  // Initial Load
  useEffect(() => {
    loadSheets();
    const interval = setInterval(async () => {
        const status = await checkHealth();
        setIsOnline(status);
    }, 15000);
    return () => clearInterval(interval);
  }, []);

  const loadSheets = async () => {
    const data = await getSheets();
    setSheets(data);
    // Refresh active sheet if it exists in the new list
    if (activeSheet) {
        const updated = data.find(s => s.id === activeSheet.id);
        if (updated) setActiveSheet(updated);
    }
  };

  const handleCreateSheet = () => {
    const newSheet = createNewSheet(`ุนูุฏุฉ ุฌุฏูุฏุฉ ${sheets.length + 1}`, 0, DEFAULT_USER.id);
    setActiveSheet(newSheet);
    // Don't save yet, wait for user input
  };

  const handleSelectSheet = (sheet: Sheet) => {
    setActiveSheet(sheet);
    setFilterReason('ALL'); // Reset filter when switching
  };

  const handleUpdateSheetHeader = (updated: Sheet) => {
    setActiveSheet(updated);
    // Auto-save logic could go here with debounce, but we have a manual save button for now
  };

  const handleSaveSheet = async () => {
    if (!activeSheet) return;
    setIsSaving(true);
    try {
        const saved = await saveSheet(activeSheet);
        await loadSheets(); // Refresh list
        setActiveSheet(saved);
    } catch (error) {
        console.error("Save failed", error);
        alert("ูุดู ุงูุญูุธ. ูุฑุฌู ุงูุชุญูู ูู ุงูุงุชุตุงู.");
    } finally {
        setIsSaving(false);
    }
  };

  const handleAddLine = async (line: ExpenseLine) => {
      if (!activeSheet) return;
      const updatedSheet = {
          ...activeSheet,
          lines: [...activeSheet.lines, line]
      };
      setActiveSheet(updatedSheet);
      // Optimistic update, but we also save to ensure persistence
      await saveSheet(updatedSheet);
      loadSheets(); // Background refresh
  };

  const handleUpdateLine = async (updatedLine: ExpenseLine) => {
      if (!activeSheet) return;
      const updatedSheet = {
          ...activeSheet,
          lines: activeSheet.lines.map(l => l.id === updatedLine.id ? updatedLine : l)
      };
      setActiveSheet(updatedSheet);
      await saveSheet(updatedSheet);
      loadSheets();
  };

  const handleDeleteLine = async (lineId: string) => {
      if (!activeSheet) return;
      if (!window.confirm("ูู ุฃูุช ูุชุฃูุฏ ูู ุญุฐู ูุฐุง ุงูุณุทุฑุ")) return;

      const updatedSheet = {
          ...activeSheet,
          lines: activeSheet.lines.filter(l => l.id !== lineId)
      };
      setActiveSheet(updatedSheet);
      await saveSheet(updatedSheet);
      loadSheets();
  };

  return (
    <div className="flex flex-col h-screen bg-slate-50 font-sans text-slate-800">
      <ConnectionBar isOnline={isOnline} />

      <main className="flex-1 flex overflow-hidden p-4 gap-4">
        {/* Sidebar */}
        <div className="w-64 flex-shrink-0 hidden md:block h-full">
          <SheetList 
            sheets={sheets} 
            activeSheetId={activeSheet?.id || null} 
            onSelectSheet={handleSelectSheet}
            onRefresh={loadSheets}
            onCreateNew={handleCreateSheet}
          />
        </div>

        {/* Main Content */}
        <div className="flex-1 flex flex-col h-full overflow-hidden">
          {!activeSheet ? (
            <div className="flex-1 flex flex-col items-center justify-center bg-white rounded-lg border border-dashed border-slate-300 text-slate-400">
              <div className="w-16 h-16 bg-slate-100 rounded-full flex items-center justify-center mb-4">
                <Filter size={32} />
              </div>
              <h2 className="text-xl font-bold text-slate-600">ุงุฎุชุฑ ุนูุฏุฉ ููุจุฏุก</h2>
              <p className="mt-2 text-sm">ุฃู ูู ุจุฅูุดุงุก ุนูุฏุฉ ุฌุฏูุฏุฉ ูู ุงููุงุฆูุฉ ุงูุฌุงูุจูุฉ</p>
              <button 
                onClick={handleCreateSheet}
                className="mt-6 px-6 py-2 bg-primary text-white rounded-full hover:bg-blue-700 transition shadow-sm"
              >
                ุฅูุดุงุก ุนูุฏุฉ ุฌุฏูุฏุฉ
              </button>
            </div>
          ) : (
            <>
              <SheetHeader 
                sheet={activeSheet}
                onUpdateSheet={handleUpdateSheetHeader}
                onSave={handleSaveSheet}
                onExport={() => exportToCSV(activeSheet)}
                isSaving={isSaving}
              />

              {/* Filter Tabs */}
              <div className="flex items-center gap-2 mb-2 overflow-x-auto pb-2 custom-scrollbar">
                <span className="text-xs font-bold text-slate-500 whitespace-nowrap ml-2">ุชุตููุฉ ุญุณุจ:</span>
                <button
                    onClick={() => setFilterReason('ALL')}
                    className={`px-3 py-1 rounded-full text-xs font-medium transition-colors whitespace-nowrap ${
                        filterReason === 'ALL' 
                        ? 'bg-slate-800 text-white' 
                        : 'bg-white border border-slate-200 text-slate-600 hover:bg-slate-100'
                    }`}
                >
                    ุงููู
                </button>
                {REASON_OPTIONS.map(reason => (
                     <button
                        key={reason}
                        onClick={() => setFilterReason(reason)}
                        className={`px-3 py-1 rounded-full text-xs font-medium transition-colors whitespace-nowrap ${
                            filterReason === reason
                            ? 'bg-blue-600 text-white shadow-md'
                            : 'bg-white border border-slate-200 text-slate-600 hover:bg-slate-50'
                        }`}
                    >
                        {reason}
                    </button>
                ))}
              </div>

              <div className="flex-1 min-h-0">
                  <ExpenseTable 
                    sheet={activeSheet}
                    onAddLine={handleAddLine}
                    onUpdateLine={handleUpdateLine}
                    onDeleteLine={handleDeleteLine}
                    reasonFilter={filterReason}
                  />
              </div>
            </>
          )}
        </div>
      </main>
    </div>
  );
};

export default App;

