          <SheetList 
            sheets={sheets} 
            activeSheetId={activeSheet?.id || null} 
            onSelectSheet={handleSelectSheet}
            onRefresh={loadSheets}
            onCreateNew={handleCreateSheet}
          />
        </div>

        {/* Main Content */}
        <div className="flex-1 flex flex-col h-full overflow-hidden">
          {!activeSheet ? (
            <div className="flex-1 flex flex-col items-center justify-center bg-white rounded-xl border border-dashed border-slate-200 text-slate-400">
