          <div className="col-span-2 sm:col-span-1">
            <label className="block text-[11px] font-semibold text-slate-400 mb-1.5 uppercase tracking-wide">ุฑูู/ุงุณู ุงูุนูุฏุฉ</label>
            <input
              type="text"
              value={sheet.custody_number}
              onChange={(e) => handleChange('custody_number', e.target.value)}
              className="w-full px-3 py-2.5 bg-white border border-slate-200 rounded-lg focus:ring-4 focus:ring-blue-500/10 focus:border-blue-500 transition-all font-medium text-slate-700 placeholder:text-slate-300 shadow-sm"
              placeholder="ูุซุงู: ุนูุฏุฉ ูุดุฑูุน ุงูุฑูุงุถ"
            />
          </div>
          <div className="col-span-2 sm:col-span-1">
            <label className="block text-[11px] font-semibold text-slate-400 mb-1.5 uppercase tracking-wide">ูุจูุบ ุงูุนูุฏุฉ</label>
            <input
              type="number"
              value={sheet.custody_amount || ''}
              onChange={(e) => handleChange('custody_amount', parseFloat(e.target.value) || 0)}
              className="w-full px-3 py-2.5 bg-white border border-slate-200 rounded-lg focus:ring-4 focus:ring-blue-500/10 focus:border-blue-500 transition-all font-mono font-medium text-slate-700 placeholder:text-slate-300 shadow-sm"
              placeholder="0.00"
            />
          </div>
        </div>

        {/* Stats Cards */}
        <div className="md:col-span-4 grid grid-cols-2 gap-4">
          <div className="p-3 bg-white rounded-xl border border-slate-100 shadow-sm flex flex-col justify-center">
            <p className="text-[11px] font-medium text-slate-400 mb-1">ุงูุฅุฌูุงูู ุงูููุตุฑู</p>
            <p className="text-xl font-bold text-slate-700 font-mono tracking-tight">{formatCurrency(totalSpent)}</p>
          </div>
          <div className={`p-3 rounded-xl border shadow-sm flex flex-col justify-center ${remainingColor} transition-colors duration-300`}>
             <div className="flex justify-between items-center mb-1">
                <p className="text-[11px] font-medium opacity-80">ุงููุชุจูู</p>
                {remaining < 0 && <AlertTriangle size={14} className="text-rose-500" />}
             </div>
            <p className="text-xl font-bold font-mono tracking-tight">{formatCurrency(remaining)}</p>
          </div>
        </div>

        {/* Actions */}
        <div className="md:col-span-3 flex flex-col justify-center h-full gap-3 pl-2">
           <button 
            onClick={onSave}
            disabled={isSaving}
            className="flex items-center justify-center gap-2 w-full bg-blue-600 hover:bg-blue-700 active:bg-blue-800 text-white py-2.5 px-4 rounded-lg shadow-blue-200 shadow-md transition-all text-sm font-semibold disabled:opacity-70 disabled:shadow-none"
          >
            {isSaving ? <div className="animate-spin h-4 w-4 border-2 border-white border-t-transparent rounded-full"/> : <Save size={18} />}
            ุญูุธ ุงูุนูุฏุฉ
          </button>

          <div className="flex gap-2">
             <button 
              onClick={onExport}
              className="flex-1 flex items-center justify-center gap-1.5 bg-white border border-slate-200 text-slate-600 hover:bg-slate-50 hover:border-slate-300 py-2 px-3 rounded-lg text-xs font-medium transition-all"
            >
