    if (!draft.company || !draft.description || draft.amount <= 0) {
      showToast("ูุฑุฌู ุชุนุจุฆุฉ: ุงูุดุฑูุฉุ ุงูุจูุงูุ ุงููุจูุบ", 'error');
      return;
