import React, { useMemo } from 'react';
import { Sheet, ExpenseReason } from '../types';
import { formatCurrency } from '../utils';
import { Save, Lock, Download, AlertTriangle, PieChart } from 'lucide-react';
import { REASON_COLORS } from '../constants';

interface SheetHeaderProps {
  sheet: Sheet;
  onUpdateSheet: (updated: Sheet) => void;
  onSave: () => void;
  onExport: () => void;
  isSaving: boolean;
}

export const SheetHeader: React.FC<SheetHeaderProps> = ({ 
  sheet, 
  onUpdateSheet, 
  onSave, 
  onExport,
  isSaving 
}) => {
  const totalSpent = sheet.lines.reduce((sum, line) => sum + line.amount + (line.bank_fees || 0), 0);
  const remaining = sheet.custody_amount - totalSpent;
  const progress = sheet.custody_amount > 0 ? (totalSpent / sheet.custody_amount) * 100 : 0;

  // Calculate analytics
  const analytics = useMemo(() => {
    const breakdown: Record<string, number> = {};
    sheet.lines.forEach(line => {
      breakdown[line.reason] = (breakdown[line.reason] || 0) + line.amount + (line.bank_fees || 0);
    });

    // Sort by amount desc
    return Object.entries(breakdown)
      .sort(([, a], [, b]) => b - a)
      .map(([reason, amount]) => ({
        reason: reason as ExpenseReason,
        amount,
        percent: totalSpent > 0 ? (amount / totalSpent) * 100 : 0
      }));
  }, [sheet.lines, totalSpent]);

  const handleChange = (field: keyof Sheet, value: any) => {
    onUpdateSheet({ ...sheet, [field]: value });
  };

  // Modern, softer color palette for remaining amount
  const remainingColor = remaining < 0 
    ? 'text-rose-600 bg-rose-50 border-rose-100' 
    : remaining < (sheet.custody_amount * 0.2) 
      ? 'text-amber-600 bg-amber-50 border-amber-100' 
      : 'text-emerald-600 bg-emerald-50 border-emerald-100';

  // Extract pure bg color class from the complex Tailwind string in constants
  const getBgColor = (reason: ExpenseReason) => {
    const classes = REASON_COLORS[reason] || '';
    const bgClass = classes.split(' ').find(c => c.startsWith('bg-'));
    return bgClass ? bgClass.replace('bg-', 'bg-').replace('-50', '-500') : 'bg-slate-400';
  };

  return (
    <div className="bg-white rounded-xl shadow-[0_2px_10px_-3px_rgba(6,81,237,0.1)] border border-slate-100 p-3 md:p-5 mb-3 transition-all group hover:shadow-md">
      <div className="grid grid-cols-1 md:grid-cols-12 gap-3 md:gap-6 items-start">

        {/* Form Inputs */}
        <div className="md:col-span-5 grid grid-cols-2 gap-3 md:gap-4">
          <div className="col-span-2 sm:col-span-1">
            <label className="block text-[10px] md:text-[11px] font-semibold text-slate-400 mb-1.5 uppercase tracking-wide">ุฑูู/ุงุณู ุงูุนูุฏุฉ</label>
            <input
              type="text"
              value={sheet.custody_number}
              onChange={(e) => handleChange('custody_number', e.target.value)}
              className="w-full px-3 py-2.5 bg-white border border-slate-200 rounded-lg focus:ring-4 focus:ring-blue-500/10 focus:border-blue-500 transition-all font-medium text-slate-700 placeholder:text-slate-300 shadow-sm text-sm"
              placeholder="ูุซุงู: ุนูุฏุฉ ูุดุฑูุน ุงูุฑูุงุถ"
            />
          </div>
          <div className="col-span-2 sm:col-span-1">
            <label className="block text-[10px] md:text-[11px] font-semibold text-slate-400 mb-1.5 uppercase tracking-wide">ูุจูุบ ุงูุนูุฏุฉ</label>
            <input
              type="number"
              value={sheet.custody_amount || ''}
              onChange={(e) => handleChange('custody_amount', parseFloat(e.target.value) || 0)}
              className="w-full px-3 py-2.5 bg-white border border-slate-200 rounded-lg focus:ring-4 focus:ring-blue-500/10 focus:border-blue-500 transition-all font-mono font-medium text-slate-700 placeholder:text-slate-300 shadow-sm text-sm"
              placeholder="0.00"
            />
          </div>
        </div>

        {/* Stats Cards */}
        <div className="md:col-span-4 grid grid-cols-2 gap-3 md:gap-4">
          <div className="p-2 md:p-3 bg-white rounded-xl border border-slate-100 shadow-sm flex flex-col justify-center">
            <p className="text-[10px] md:text-[11px] font-medium text-slate-400 mb-1">ุงูุฅุฌูุงูู ุงูููุตุฑู</p>
            <p className="text-lg md:text-xl font-bold text-slate-700 font-mono tracking-tight">{formatCurrency(totalSpent)}</p>
          </div>
          <div className={`p-2 md:p-3 rounded-xl border shadow-sm flex flex-col justify-center ${remainingColor} transition-colors duration-300`}>
             <div className="flex justify-between items-center mb-1">
                <p className="text-[10px] md:text-[11px] font-medium opacity-80">ุงููุชุจูู</p>
                {remaining < 0 && <AlertTriangle size={14} className="text-rose-500" />}
             </div>
            <p className="text-lg md:text-xl font-bold font-mono tracking-tight">{formatCurrency(remaining)}</p>
          </div>
        </div>

        {/* Actions */}
        <div className="md:col-span-3 flex md:flex-col justify-center h-full gap-2 md:gap-3 md:pl-2 mt-2 md:mt-0">
           <button 
            onClick={onSave}
            disabled={isSaving}
            className="flex-1 flex items-center justify-center gap-2 bg-blue-600 hover:bg-blue-700 active:bg-blue-800 text-white py-2.5 px-4 rounded-lg shadow-blue-200 shadow-md transition-all text-sm font-semibold disabled:opacity-70 disabled:shadow-none"
          >
            {isSaving ? <div className="animate-spin h-4 w-4 border-2 border-white border-t-transparent rounded-full"/> : <Save size={18} />}
            <span className="md:inline">ุญูุธ</span>
          </button>

          <div className="flex gap-2 flex-1">
             <button 
              onClick={onExport}
              className="flex-1 flex items-center justify-center gap-1.5 bg-white border border-slate-200 text-slate-600 hover:bg-slate-50 hover:border-slate-300 py-2 px-3 rounded-lg text-xs font-medium transition-all"
            >
              <Download size={14} />
              ุชุตุฏูุฑ
            </button>
            <button 
              className="flex-1 flex items-center justify-center gap-1.5 bg-white border border-slate-200 text-slate-400 py-2 px-3 rounded-lg text-xs font-medium cursor-not-allowed"
              title="ูุชู ุบูู ุงูุนูุฏุฉ ุจุนุฏ ุงููุฑุงุฌุนุฉ"
            >
              <Lock size={14} />
              ุฅุบูุงู
            </button>
          </div>
        </div>
      </div>

      {/* Analytics Distribution Bar */}
      <div className="mt-4 md:mt-6">
        <div className="flex justify-between items-center mb-2">
           <div className="flex items-center gap-1.5">
             <PieChart size={12} className="text-slate-400"/>
             <span className="text-[10px] font-bold text-slate-400 uppercase tracking-wider">ุชุญููู ุงูุตุฑู</span>
           </div>
           <span className="text-[10px] font-mono text-slate-400">{Math.min(progress, 100).toFixed(1)}% ูุณุชุฎุฏู</span>
        </div>

        {/* The Multi-color Bar */}
        <div className="h-2.5 w-full bg-slate-100 rounded-full overflow-hidden flex">
          {analytics.length > 0 ? (
            analytics.map((item, idx) => (
              <div 
                key={item.reason}
                className={`h-full ${getBgColor(item.reason)} hover:opacity-80 transition-all duration-500 relative group/bar`}
                style={{ width: `${item.percent}%` }}
                title={`${item.reason}: ${formatCurrency(item.amount)} (${item.percent.toFixed(1)}%)`}
              />
            ))
          ) : (
             <div className="w-0" />
          )}
        </div>

        {/* Legend (Top 4 items) */}
        {analytics.length > 0 && (
          <div className="flex flex-wrap gap-2 md:gap-3 mt-2">
            {analytics.slice(0, 4).map(item => (
              <div key={item.reason} className="flex items-center gap-1.5">
                <div className={`w-2 h-2 rounded-full ${getBgColor(item.reason)}`}></div>
                <span className="text-[9px] md:text-[10px] text-slate-500 truncate max-w-[80px] md:max-w-[100px]">{item.reason}</span>
                <span className="text-[9px] md:text-[10px] font-mono text-slate-400">({Math.round(item.percent)}%)</span>
              </div>
            ))}
          </div>
        )}
      </div>
    </div>
  );
};

import React, { useState, useEffect, useRef } from 'react';
import { Sheet, ExpenseLine, ExpenseReason } from './types';
import { getSheets, saveSheet, createNewSheet, checkHealth } from './services/expenseService';
import { DEFAULT_USER, REASON_OPTIONS } from './constants';
import { exportToCSV } from './utils';

import { ConnectionBar } from './components/ConnectionBar';
import { SheetList } from './components/SheetList';
import { SheetHeader } from './components/SheetHeader';
import { ExpenseTable } from './components/ExpenseTable';
import { ToastContainer, ToastMessage, ToastType } from './components/Toast';
import { Filter, GripHorizontal, FileText, Menu, X } from 'lucide-react';

const App: React.FC = () => {
  const [isOnline, setIsOnline] = useState(true);
  const [sheets, setSheets] = useState<Sheet[]>([]);
  const [activeSheet, setActiveSheet] = useState<Sheet | null>(null);
  const [isSaving, setIsSaving] = useState(false);
  const [filterReason, setFilterReason] = useState<ExpenseReason | 'ALL'>('ALL');

  // Mobile UI State
  const [isSidebarOpen, setIsSidebarOpen] = useState(false);

  // Toast State
  const [toasts, setToasts] = useState<ToastMessage[]>([]);

  // Resizable Notes State
  const [notesHeight, setNotesHeight] = useState(100); 
  const isResizing = useRef(false);

  // Initial Load
  useEffect(() => {
    loadSheets();
    const interval = setInterval(async () => {
        const status = await checkHealth();
        setIsOnline(status);
    }, 15000);
    return () => clearInterval(interval);
  }, []);

  const loadSheets = async () => {
    const data = await getSheets();
    setSheets(data);
    if (activeSheet) {
        const updated = data.find(s => s.id === activeSheet.id);
        if (updated) setActiveSheet(updated);
    }
  };

  // Toast Helpers
  const showToast = (message: string, type: ToastType = 'success') => {
    const id = Math.random().toString(36).substr(2, 9);
    setToasts(prev => [...prev, { id, message, type }]);
  };

  const dismissToast = (id: string) => {
    setToasts(prev => prev.filter(t => t.id !== id));
  };

  const handleCreateSheet = () => {
    const newSheet = createNewSheet(`ุนูุฏุฉ ุฌุฏูุฏุฉ ${sheets.length + 1}`, 0, DEFAULT_USER.id);
    setActiveSheet(newSheet);
    showToast('ุชู ุฅูุดุงุก ูุณูุฏุฉ ุนูุฏุฉ ุฌุฏูุฏุฉ', 'success');
    setIsSidebarOpen(false); // Close sidebar on mobile
  };

  const handleSelectSheet = (sheet: Sheet) => {
    setActiveSheet(sheet);
    setFilterReason('ALL');
    setIsSidebarOpen(false); // Close sidebar on mobile
  };

  const handleUpdateSheetHeader = (updated: Sheet) => {
    setActiveSheet(updated);
  };

  const handleSaveSheet = async () => {
    if (!activeSheet) return;
    setIsSaving(true);
    try {
        const saved = await saveSheet(activeSheet);
        await loadSheets();
        setActiveSheet(saved);
        showToast('ุชู ุญูุธ ุงูุนูุฏุฉ ุจูุฌุงุญ');
    } catch (error) {
        console.error("Save failed", error);
        showToast('ูุดู ุงูุญูุธ. ูุฑุฌู ุงูุชุญูู ูู ุงูุงุชุตุงู.', 'error');
    } finally {
        setIsSaving(false);
    }
  };

  const handleAddLine = async (line: ExpenseLine) => {
      if (!activeSheet) return;
      const updatedSheet = {
          ...activeSheet,
          lines: [...activeSheet.lines, line]
      };
      setActiveSheet(updatedSheet);
      await saveSheet(updatedSheet);
      loadSheets();
      showToast('ุชู ุฅุถุงูุฉ ุงูุจูุฏ ุจูุฌุงุญ');
  };

  const handleUpdateLine = async (updatedLine: ExpenseLine) => {
      if (!activeSheet) return;
      const updatedSheet = {
          ...activeSheet,
          lines: activeSheet.lines.map(l => l.id === updatedLine.id ? updatedLine : l)
      };
      setActiveSheet(updatedSheet);
      await saveSheet(updatedSheet);
      loadSheets();
      showToast('ุชู ุชุญุฏูุซ ุงูุจูุฏ');
  };

  const handleDeleteLine = async (lineId: string) => {
      if (!activeSheet) return;
      if (!window.confirm("ูู ุฃูุช ูุชุฃูุฏ ูู ุญุฐู ูุฐุง ุงูุณุทุฑุ")) return;

      const updatedSheet = {
          ...activeSheet,
          lines: activeSheet.lines.filter(l => l.id !== lineId)
      };
      setActiveSheet(updatedSheet);
      await saveSheet(updatedSheet);
      loadSheets();
      showToast('ุชู ุญุฐู ุงูุจูุฏ', 'success');
  };

  // Resize Handler Logic
  const startResizing = (e: React.MouseEvent) => {
    isResizing.current = true;
    const startY = e.clientY;
    const startHeight = notesHeight;

    const onMouseMove = (moveEvent: MouseEvent) => {
      if (!isResizing.current) return;
      const newHeight = startHeight + (moveEvent.clientY - startY);
      if (newHeight >= 40 && newHeight <= 400) {
        setNotesHeight(newHeight);
      }
    };

    const onMouseUp = () => {
      isResizing.current = false;
      document.removeEventListener('mousemove', onMouseMove);
      document.removeEventListener('mouseup', onMouseUp);
      document.body.style.cursor = 'default';
    };

    document.addEventListener('mousemove', onMouseMove);
    document.addEventListener('mouseup', onMouseUp);
    document.body.style.cursor = 'row-resize';
  };

  return (
    <div className="flex flex-col h-screen bg-[#f8fafc] font-sans text-slate-800 overflow-hidden">
      {/* Header with Mobile Menu Toggle */}
      <div className="flex-shrink-0 relative z-20">
         <ConnectionBar isOnline={isOnline} />
         <button 
            onClick={() => setIsSidebarOpen(!isSidebarOpen)}
            className="md:hidden absolute top-1.5 right-3 text-white p-1.5 rounded-md hover:bg-white/10"
         >
            {isSidebarOpen ? <X size={18} /> : <Menu size={18} />}
         </button>
      </div>

      <ToastContainer toasts={toasts} onDismiss={dismissToast} />

      {/* Reduced padding and gap for mobile */}
      <main className="flex-1 flex overflow-hidden p-2 md:p-5 gap-2 md:gap-5 relative">

        {/* Mobile Sidebar Overlay */}
        {isSidebarOpen && (
          <div 
            className="fixed inset-0 bg-black/50 z-30 md:hidden backdrop-blur-sm"
            onClick={() => setIsSidebarOpen(false)}
          />
        )}

        {/* Sidebar */}
        <div className={`
            fixed inset-y-0 right-0 z-40 w-72 transform transition-transform duration-300 ease-in-out bg-white shadow-2xl md:translate-x-0 md:static md:w-64 md:shadow-none md:bg-transparent md:block
            ${isSidebarOpen ? 'translate-x-0' : 'translate-x-full'}
        `}>
          <div className="h-full pt-14 md:pt-0"> {/* Padding top for mobile header */}
             <SheetList 
                sheets={sheets} 
                activeSheetId={activeSheet?.id || null} 
                onSelectSheet={handleSelectSheet}
                onRefresh={loadSheets}
                onCreateNew={handleCreateSheet}
             />
          </div>
        </div>

        {/* Main Content */}
        <div className="flex-1 flex flex-col h-full overflow-hidden w-full">
          {!activeSheet ? (
            <div className="flex-1 flex flex-col items-center justify-center bg-white rounded-xl border border-dashed border-slate-200 text-slate-400 m-2">
              <div className="w-16 h-16 md:w-20 md:h-20 bg-slate-50 rounded-full flex items-center justify-center mb-4 md:mb-5 shadow-inner">
                <Filter size={28} className="text-slate-300" />
              </div>
              <h2 className="text-lg md:text-xl font-bold text-slate-600">ุงุฎุชุฑ ุนูุฏุฉ ููุจุฏุก</h2>
              <p className="mt-2 text-xs md:text-sm text-slate-400 text-center px-4">ุฃู ุงูุชุญ ุงููุงุฆูุฉ ูุงุฎุชูุงุฑ ุนูุฏุฉ ุณุงุจูุฉ</p>
              <button 
                onClick={handleCreateSheet}
                className="mt-6 md:mt-8 px-6 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700 transition shadow-lg shadow-blue-200 text-xs md:text-sm font-semibold"
              >
                ุฅูุดุงุก ุนูุฏุฉ ุฌุฏูุฏุฉ
              </button>
            </div>
          ) : (
            <>
              {/* Top Header (Compact on mobile) */}
              <div className="flex-shrink-0">
                <SheetHeader 
                    sheet={activeSheet}
                    onUpdateSheet={handleUpdateSheetHeader}
                    onSave={handleSaveSheet}
                    onExport={() => exportToCSV(activeSheet)}
                    isSaving={isSaving}
                />

                {/* Filter Tabs - Compact */}
                <div className="flex items-center gap-1.5 mb-2 overflow-x-auto pb-1 custom-scrollbar px-1 min-h-[32px]">
                    <span className="text-[10px] font-bold text-slate-400 whitespace-nowrap ml-1 uppercase tracking-wide">ุชุตููุฉ:</span>
                    <button
                        onClick={() => setFilterReason('ALL')}
                        className={`px-2.5 py-1 rounded-md text-[10px] md:text-xs font-medium transition-all whitespace-nowrap ${
                            filterReason === 'ALL' 
                            ? 'bg-slate-800 text-white shadow-sm' 
                            : 'bg-white border border-slate-200 text-slate-500'
                        }`}
                    >
                        ุงููู
                    </button>
                    {REASON_OPTIONS.map(reason => (
