import React, { useMemo } from 'react';
import { Sheet, ExpenseReason } from '../types';
import { formatCurrency } from '../utils';
import { Save, Lock, Download, AlertTriangle, PieChart } from 'lucide-react';
import { REASON_COLORS } from '../constants';

interface SheetHeaderProps {
  sheet: Sheet;
  onUpdateSheet: (updated: Sheet) => void;
  onSave: () => void;
  onExport: () => void;
  isSaving: boolean;
}

export const SheetHeader: React.FC<SheetHeaderProps> = ({ 
  sheet, 
  onUpdateSheet, 
  onSave, 
  onExport,
  isSaving 
}) => {
  const totalSpent = sheet.lines.reduce((sum, line) => sum + line.amount + (line.bank_fees || 0), 0);
  const remaining = sheet.custody_amount - totalSpent;
  const progress = sheet.custody_amount > 0 ? (totalSpent / sheet.custody_amount) * 100 : 0;

  // Calculate analytics
  const analytics = useMemo(() => {
    const breakdown: Record<string, number> = {};
    sheet.lines.forEach(line => {
      breakdown[line.reason] = (breakdown[line.reason] || 0) + line.amount + (line.bank_fees || 0);
    });

    // Sort by amount desc
    return Object.entries(breakdown)
      .sort(([, a], [, b]) => b - a)
      .map(([reason, amount]) => ({
        reason: reason as ExpenseReason,
        amount,
        percent: totalSpent > 0 ? (amount / totalSpent) * 100 : 0
      }));
  }, [sheet.lines, totalSpent]);

  const handleChange = (field: keyof Sheet, value: any) => {
    onUpdateSheet({ ...sheet, [field]: value });
  };

  const remainingColor = remaining < 0 
    ? 'text-rose-600 bg-rose-50 border-rose-100' 
    : remaining < (sheet.custody_amount * 0.2) 
      ? 'text-amber-600 bg-amber-50 border-amber-100' 
      : 'text-emerald-600 bg-emerald-50 border-emerald-100';

  const getBgColor = (reason: ExpenseReason) => {
    const classes = REASON_COLORS[reason] || '';
    const bgClass = classes.split(' ').find(c => c.startsWith('bg-'));
    return bgClass ? bgClass.replace('bg-', 'bg-').replace('-50', '-500') : 'bg-slate-400';
  };

  return (
    <div className="bg-white rounded-xl shadow-[0_2px_10px_-3px_rgba(6,81,237,0.1)] border border-slate-100 p-3 mb-2 transition-all">
      <div className="grid grid-cols-2 md:grid-cols-12 gap-2 md:gap-4 items-start">

        {/* Form Inputs */}
        <div className="col-span-2 md:col-span-5 grid grid-cols-2 gap-2">
          <div className="col-span-1">
            <label className="block text-[9px] md:text-[11px] font-semibold text-slate-400 mb-1 uppercase tracking-wide">ุงูุนูุฏุฉ</label>
            <input
              type="text"
              value={sheet.custody_number}
              onChange={(e) => handleChange('custody_number', e.target.value)}
              className="w-full px-2 py-1.5 md:py-2 bg-white border border-slate-200 rounded-md focus:ring-2 focus:ring-blue-500/10 focus:border-blue-500 transition-all font-medium text-slate-700 placeholder:text-slate-300 text-[11px] md:text-sm"
              placeholder="ุงุณู ุงูุนูุฏุฉ"
            />
          </div>
          <div className="col-span-1">
            <label className="block text-[9px] md:text-[11px] font-semibold text-slate-400 mb-1 uppercase tracking-wide">ุงููุจูุบ</label>
            <input
              type="number"
              value={sheet.custody_amount || ''}
              onChange={(e) => handleChange('custody_amount', parseFloat(e.target.value) || 0)}
              className="w-full px-2 py-1.5 md:py-2 bg-white border border-slate-200 rounded-md focus:ring-2 focus:ring-blue-500/10 focus:border-blue-500 transition-all font-mono font-medium text-slate-700 placeholder:text-slate-300 text-[11px] md:text-sm"
              placeholder="0.00"
            />
          </div>
        </div>

        {/* Stats Cards - Very Compact on Mobile */}
        <div className="col-span-2 md:col-span-4 grid grid-cols-2 gap-2">
          <div className="p-1.5 md:p-2 bg-white rounded-lg border border-slate-100 shadow-sm flex flex-col justify-center">
            <p className="text-[9px] md:text-[11px] font-medium text-slate-400 mb-0.5">ุงูููุตุฑู</p>
            <p className="text-sm md:text-lg font-bold text-slate-700 font-mono tracking-tight">{formatCurrency(totalSpent)}</p>
          </div>
          <div className={`p-1.5 md:p-2 rounded-lg border shadow-sm flex flex-col justify-center ${remainingColor}`}>
             <div className="flex justify-between items-center mb-0.5">
                <p className="text-[9px] md:text-[11px] font-medium opacity-80">ุงููุชุจูู</p>
                {remaining < 0 && <AlertTriangle size={12} className="text-rose-500" />}
             </div>
            <p className="text-sm md:text-lg font-bold font-mono tracking-tight">{formatCurrency(remaining)}</p>
          </div>
        </div>

        {/* Actions - Horizontal on mobile */}
        <div className="col-span-2 md:col-span-3 flex md:flex-col justify-between h-full gap-2 mt-1 md:mt-0">
           <button 
            onClick={onSave}
            disabled={isSaving}
            className="flex-1 flex items-center justify-center gap-1.5 bg-blue-600 hover:bg-blue-700 active:bg-blue-800 text-white py-1.5 md:py-2 px-3 rounded-lg shadow-sm transition-all text-xs md:text-sm font-semibold disabled:opacity-70"
          >
            {isSaving ? <div className="animate-spin h-3 w-3 border-2 border-white border-t-transparent rounded-full"/> : <Save size={14} md:size={16} />}
            <span>ุญูุธ</span>
          </button>

          <div className="flex gap-2 flex-1">
             <button 
              onClick={onExport}
              className="flex-1 flex items-center justify-center gap-1 bg-white border border-slate-200 text-slate-600 hover:bg-slate-50 py-1.5 px-2 rounded-lg text-[10px] md:text-xs font-medium transition-all"
            >
              <Download size={12} />
              <span className="md:inline">ุชุตุฏูุฑ</span>
            </button>
            <button 
              className="flex-1 flex items-center justify-center gap-1 bg-white border border-slate-200 text-slate-400 py-1.5 px-2 rounded-lg text-[10px] md:text-xs font-medium cursor-not-allowed"
            >
              <Lock size={12} />
              <span className="md:inline">ุฅุบูุงู</span>
            </button>
          </div>
        </div>
      </div>

      {/* Analytics Bar - Thin line */}
      <div className="mt-3 md:mt-4">
        <div className="h-1.5 md:h-2 w-full bg-slate-100 rounded-full overflow-hidden flex">
          {analytics.length > 0 ? (
            analytics.map((item) => (
              <div 
                key={item.reason}
                className={`h-full ${getBgColor(item.reason)}`}
                style={{ width: `${item.percent}%` }}
              />
            ))
          ) : (
             <div className="w-0" />
          )}
        </div>
        {/* Legend - Only show top 3 on mobile */}
        {analytics.length > 0 && (
          <div className="flex flex-wrap gap-2 mt-1.5">
            {analytics.slice(0, 3).map(item => (
              <div key={item.reason} className="flex items-center gap-1">
                <div className={`w-1.5 h-1.5 rounded-full ${getBgColor(item.reason)}`}></div>
                <span className="text-[8px] md:text-[10px] text-slate-500 truncate max-w-[60px]">{item.reason}</span>
                <span className="text-[8px] md:text-[10px] font-mono text-slate-400">({Math.round(item.percent)}%)</span>
              </div>
            ))}
          </div>
        )}
      </div>
    </div>
  );
};

